const weiboCrawler = require('../index')
const fs = require('fs')

// const userInfo = 'http://weibo.com/p/1005051736338681/home?from=page_100505_profile&wvr=6&mod=data#place'
const userInfo = 'http://weibo.com/u/1736338681?topnav=1&wvr=6&topsug=1'
// const userInfo = 1736338681

weiboCrawler(userInfo, 5)
  .then(data => {
    fs.writeFile(`${__dirname}/data.json`, JSON.stringify(data, null, 2), (err) => {
      if (err) return err
      console.log(`data has been saved to ${__dirname}/data.json`)
    })
  })
  .catch(console.error)